pub enum Frame<T, B, E> {

    /// Either a request or a response
    Message {
        /// The message value
        message: T,
        /// Set to true when body frames will follow
        body: bool,
    },

    /// Body frame. None indicates that the body is done streaming.
    Body {
        /// Body chunk. Setting to `None` indicates that the body is done
        /// streaming and there will be no further body frames sent with the
        /// given request ID.
        chunk: Option<B>,
    },

    /// Error
    Error {
        /// Error value
        error: E,
    },
}

type Transport:
        Transport<Item = Frame<Self::Response, Self::ResponseBody, Self::Error>,
                  SinkItem = Frame<Self::Request, Self::RequestBody, Self::Error>>;

type Transport:
